using namespace std;

#define fi first
#define se second
#define up unordered_map
#define us unordered_set
#define endl '\n';
typedef vector<int> vi;
typedef long long ll;
typedef vector<vi> vvi;
typedef vector<ll> vll;
typedef vector<vll> vvl;


typedef pair<int,int> ii;
typedef vector<pair<int,int>> vii;
typedef vector<pair<ll,ll>> vpl;
typedef pair<ll,ll> pll;
#include <ext/pb_ds/assoc_container.hpp>
using namespace __gnu_pbds;
typedef tree<int,null_type,less<int>,rb_tree_tag,
        tree_order_statistics_node_update> indexed_set;
//s.find_by_order(ele)--for it of target
//s.order_of_key(ele)--for lower bound of target
ll m1=1000000007;
ll m2=998244353;
ll inf=1e18;
class Solution {
public:
int countOfPairs(vi& nums) {
    int n=(int)nums.size();
    vvl memo(n,vll(1001,0));
    vvl ss(n,vll(1001,0));
    for(int i=n-1;i>=0;i--){
        for(int j=0;j<=1000;j++) {
            if (j > nums[i]) {
                if(j > 0) ss[i][j]+=ss[i][j-1];
                ss[i][j]%=m1;
                continue;
            }
            if(i==n-1){
                memo[i][j]=1;
                ss[i][j]=1;
                if(j > 0) ss[i][j]+=ss[i][j-1];
                ss[i][j] %= m1;
                continue;
            }
            int l=max(j,j+nums[i+1]-nums[i]);
            int r=1000;
            if(l <= r){
                ll ans = ss[i + 1][r];
                if (l > 0) ans -= ss[i + 1][l - 1];
                ans += m1;
                ans %= m1;
                memo[i][j] = ans;
            }

            ss[i][j]=memo[i][j];
            if(j > 0) ss[i][j]+=ss[i][j-1];
            ss[i][j] %= m1;
        }
    }
    ll ans=0;
    for(int s=0;s<=1000;s++){
        ans+=memo[0][s];
        ans%=m1;
    }
    return (int)ans;
}
};
