class Solution {
public:
    int dx[8]={0,0,1,-1,1,1,-1,-1};
int dy[8]={1,-1,0,0,1,-1,-1,1};

const int N=200001;
#define ll long long int
#define pii pair<int,int>
#define all(arr) arr.begin(),arr.end()
#define fr first
#define sc second
#define pb push_back
// const int INF= 1e15;
// const int mod= 1e12+9;
const int mod= 1e9+7;
    int countOfPairs(vector<int>& arr) {
        int n= arr.size();
        int mx= *max_element(all(arr));

        vector<vector<vector<int>>>dp(n+1, vector<vector<int>>(mx+1, (vector<int>(mx+1, -1))));
        auto f=[&](auto self, int i, int curr, int prev)->int{
           // cout<<i<<" "<<curr<<" "<<prev<<endl;
           if(i>=n)return 1;

           if(dp[i][curr][prev]!=-1)
               return dp[i][curr][prev];

           int ans=0;
           int req= (arr[i]- curr);
            // cout<<i<<" "<<curr<<" "<<req<<endl;
           if(req<0){
               ans=0;
           }
           else{
                if(req<=prev){
                    ans+= self(self, i+1, curr, req);
                    ans%= mod;
                }
                if(curr+1<=arr[i]){
                  ans+= self(self, i, curr+1, prev);
                  ans%=mod;
                }
           }
           // cout<<i<<" "<<curr<<" "<<prev<<endl;
           return dp[i][curr][prev]= ans;
        };

        int ans= f(f, 0, 0, arr[0]);


        // for(int i=0; i<=mx; i++){
        //     ans= (ans+ f(f, 0, i))%mod;
        // }
        return ans;
    }
};
