class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        long long int MOD = 1e9 + 7;
        int i, j, k, p, q;
        vector<vector<long long int> > dp(n, vector<long long int>(51, 0));

        i = 0;
        for(j = 0; j <= nums[i]; j++) {
            dp[i][j] = 1;
        }

        for(i = 1; i < n; i++) {
            for(j = 0; j <= nums[i]; j++) {
                k = nums[i] - j;
                for(p = 0; p <= nums[i - 1]; p++) {
                    q = nums[i - 1] - p;
                    if(p <= j && q >= k) {
                        dp[i][j] = (dp[i][j] + dp[i - 1][p]) % MOD;
                    }
                }
            }
        }

        long long int ans = 0;
        for(j = 0; j <= nums[n - 1]; j++) {
            ans = (ans + dp[n - 1][j]) % MOD;
        }
        return ans;
    }
};
