class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        long[][] a = new long[n][1001];
        long[][] p = new long[n][1001];

        for (int i = 0; i < n; i++) {
            for (int j = 0; j <= nums[i]; j++) {
                if (i == 0) {
                    a[i][j] = 1;
                } else {
                    int m = Math.min(j, j + nums[i - 1] - nums[i]);
                    if (m >= 0) {
                        a[i][j] = p[i - 1][m];
                    } 
                }
                if (j == 0) {
                    p[i][j] = a[i][j];
                } else {
                    p[i][j] = (p[i][j - 1] + a[i][j])%1000000007;
                }

            }
        }
        return (int)(p[n - 1][nums[n - 1]]);
    }
}
