class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)

        @cache 
        def dp(i, th=0):
            if i >= n:
                return 1
            s = 0
            for j in range(th, nums[i]+1):
                if i == 0 or nums[i] - j <= nums[i-1] - th:
                    s = (s + dp(i+1, j)) % 1000000007
            return s
        return dp(0)
