class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        mod = int(1e9 + 7)
        @lru_cache(None)
        def dfs(i: int, pre1: int, pre2: int) -> int:
            if i == n:
                return 1
            cnt = 0
            left = pre1
            right = nums[i]
            for d1 in range(left, right + 1):
                d2 = nums[i] - d1
                if d2 <= pre2:
                    cnt = (cnt + dfs(i + 1, d1, d2)) % mod
            return cnt
        ans = dfs(0, 0, 100)
        dfs.cache_clear()
        return ans
