class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dp = [[1 for i in range(nums[0] + 1)]]
        prev = nums[0]

        for idx in range(1, len(nums)):
            val = nums[idx]
            temp = []
            for i in range(val + 1):
                count = 0
                for j in range(prev + 1):
                    if i >= j and val - i <= prev - j:
                        count += dp[-1][j]
                temp.append(count)
            dp.append(temp)
            prev = val

        return sum(dp[-1]) % (10**9 + 7)
