class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int N = 1e9+7;
        vector<vector<int>> dp(n + 1, vector<int> (51, 0));
        int sm  = 0;
        for(int i = n; i >= 0; i--) {
            for(int j = 0; j <= 50; j++) {
                if(i == n) dp[i][j] = 1;
                else {
                    int x = nums[i] - j;
                    if(x < 0) continue;
                    if( i == n - 1) {
                        dp[i][j] = (dp[i][j] + 1) % N;
                        continue;
                    }
                    for(int k = j; k <= 50; k++) {
                        int z = -1;
                        if(i < n - 1) z = nums[i+1] - k;
                        if(x >= z) dp[i][j] = (dp[i+1][k] + dp[i][j]) % N;
                    }
                }
            }
        }
        for(int i = 0; i <= 50; i++) sm = (sm + dp[0][i]) % N;
        return sm;
    }
};
