class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        m = max(nums)+1
        n = len(nums)
        dp = [[0]*m for i in range(n)]
        dp2 = [[0]*m for i in range(n)]
        for i in range(nums[0]+1):
            dp[0][i]=1
            dp2[0][nums[0]-i]=1
        # print(dp,dp2)
        for j in range(1,m):
            dp[0][j]+=dp[0][j-1]
            dp2[0][m-1-j]+=dp2[0][m-j]
        # print(dp,dp2)
        for i in range(1,n):
            for j in range(nums[i]+1):
                dp[i][j] += min(dp[i-1][j], dp2[i-1][nums[i]-j])
                dp2[i][j] += min(dp[i-1][nums[i]-j], dp2[i-1][j])
            for j in range(1,m):
                dp[i][j]+=dp[i][j-1]
                dp2[i][m-1-j]+=dp2[i][m-j]
        # print(dp,dp2)
        return dp[-1][nums[-1]]%(10**9+7)
