class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        @lru_cache
        def dfs(index, prev1, prev2):
            if index == len(nums):
                return 1
            result = 0
            start = 0
            if prev1 != None:
                start = prev1
            for val in range(start, nums[index]+1):
                val1 = val
                val2 = nums[index] - val1
                if (prev1 == None or val1 >= prev1) and (prev2 == None or val2 <= prev2):
                    result += dfs(index+1, val1, val2)
                    result %= (10**9+7)
            return result
        return dfs(0, None, None)
