#define rep(i,n) for(int i=0;i<n;i++)
#define rng(i,a,b) for(int i=a;i<b;i++)
#define ll long long
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = (int)nums.size();
        vector<vector<vector<ll>>> dp(n, vector<vector<ll>>(51, vector<ll>(51, 0)));
        ll mod = 1e9 + 7;
        rep(i,nums[0]+1){
            rep(j,nums[0]+1){
                if(i+j==nums[0]){
                    dp[0][i][j] = 1;
                }
            }
        }
        rng(i,1,n){
            rep(j,nums[i]+1){
                rep(k1,nums[i-1]+1){
                    if(k1>j){
                        break;
                    }
                    if(nums[i-1] - k1 >= nums[i] - j){
                        (dp[i][j][nums[i]-j]+=dp[i-1][k1][nums[i-1]-k1])%=mod;
                    }
                }
            }
        }
        ll ans = 0;
        rep(i,nums[n-1]+1){
            rep(j,nums[n-1]+1){
                if(i+j==nums[n-1]){
                    (ans+=dp[n-1][i][j])%=mod;
                }
            }
        }
        return ans;
    }
};
