from math import comb
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        addi = 0
        m = 10**9 + 7
        le = len(nums)
        for i in range(len(nums)-1):
            if nums[i+1]>nums[i]:
                addi += nums[i+1] - nums[i]
        if addi>nums[-1]:
            return 0
        def solve(n,k):
            if n<k: return 0
            if n==k: return 1
            return comb(n-1, k-1)%m + comb(n-1, k)%m

        return solve(le+nums[-1] - addi, le)%m
