class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int m = 51;
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(m, vector<int>(m, 0)));
        long long mod = 1e9 + 7;
        vector<vector<int>> prev(m, vector<int> (m, 0));
        for (int i = 0; i < n; i++) {
            for (int j = 0; j <= nums[i]; j++) {
                int a = j;
                int b = nums[i] - j;
                dp[i][a][b] = (i == 0 ? 1 : prev[a][b]);
                // cout << i << " " <<  a << " " << b << " " << dp[i][a][b] << "\n";
            }
            // cout << "........................" << "\n";
            for (int j = 0; j < m; j++) {
                for (int k = m - 1; k >= 0; k--) {
                    prev[j][k] = 0; 
                    long long curr = dp[i][j][k];
                    long long up = (j - 1 >= 0 ? prev[j - 1][k] : 0);
                    long long down = (k + 1 < m ? prev[j][k + 1] : 0);
                    long long inter = (j - 1 >=0 && k + 1 < m ? prev[j - 1][k + 1] : 0);
                    long long sum = curr + up + down - inter;
                    prev[j][k] = (sum % mod);
                }
            }
        }
        long long ans = 0;
        for (int i = 0; i <= nums[n - 1]; i++) {
            ans += dp[n - 1][i][nums[n - 1] - i];
            ans %= mod;
        }
        return ans;
    }
};
