class Solution {
public:
    int dp[2000][2000],mod=1e9+7;
    int f(int n,int ind,int prev,vector<int> &nums) {
        if(ind==n) return 1;
        if(dp[ind][prev]!=-1) return dp[ind][prev];
        int sum=0;
        for(int i=prev;i<=nums[ind];i++) {
            if(ind==0) sum=(sum+f(n,ind+1,i,nums))%mod;
            else if(nums[ind-1]-prev>=nums[ind]-i) sum=(sum+f(n,ind+1,i,nums))%mod;
        }
        return dp[ind][prev]=sum;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        int n=nums.size();
        return f(n,0,0,nums);
    }
};
