class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const long long mod = 1e9 + 7;
        int n = nums.size();
        vector<vector<long long>> dpa(nums.size() + 1, vector<long long>(51));
        auto dpb = dpa;
        for (int i = 0; i <= nums[0]; ++i) dpa[1][i] = 1;
        for (int i = 2; i <= n; ++i) {
            for (int j = 0; j <= nums[i - 1]; ++j) {
                // bug(i, j);
                for (int k = 0; k <= j; ++k) {
                    if (i - 2 >= 0 and nums[i - 2] - k < nums[i - 1] - j) continue; //break;
                    // bug(k);
                    dpa[i][j] = (dpa[i][j] + dpa[i - 1][k]) % mod;
                    // dpb[i][j] = (dpb[i][j] + dpb[i - 1][k]) % mod;
                    // bug(dpa[i][j]);
                }
            }
        }

        long long ans = 0;
        for (int i = 0; i < 51; ++i) {
            ans = (ans + dpa[n][i]) % mod;
        }
        return ans;
    }
};
