#include <bits/stdc++.h>
#define sz size()
#define bk back()
#define fi first
#define se second

using namespace std;
typedef long long ll;
typedef pair<int, int> pii;

const int MOD = 1e9 + 7;

class Solution {
  public:
    int countOfPairs(vector<int> &v) {
        int n = v.sz;

        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
        int ret = 0;

        for (int i = 0; i <= v[0]; i++)
            ret = (ret + f(1, i, v[0] - i, v, dp)) % MOD;

        return ret;
    }

    int f(int idx, int x, int y, vector<int> &v, vector<vector<vector<int>>> &dp) {
        if (idx == v.sz)
            return 1;

        if (dp[idx][x][y] != -1)
            return dp[idx][x][y];

        int ret = 0;

        for (int i = max(x, v[idx] - y); i <= v[idx]; i++) {
            int j = v[idx] - i;
            ret = (ret + f(idx + 1, i, j, v, dp)) % MOD;
        }

        return dp[idx][x][y] = ret;
    }
};
