_DEBUG = False

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MODULUS = 10 ** 9 + 7

        n = len(nums)
        A_B_dict = {
            (i, nums[-1] - i): 1
            for i in range(nums[-1] + 1)
        }
        for idx in range(n-2, -1, -1):
            num = nums[idx]
            new_A_B_dict = defaultdict(int)
            for (a, b), cnt in A_B_dict.items():
                for i in range(min(a, num-b) + 1):
                    new_A_B_dict[(i, num - i)] += cnt

            A_B_dict = {
                k: v % MODULUS
                for k, v in new_A_B_dict.items()
            }

            if _DEBUG:
                print(A_B_dict)

        return sum(A_B_dict.values()) % MODULUS
