class Solution {
public:
    int countOfPairs(std::vector<int>& nums) {
        constexpr long long M{ 1000000007 };

        int size{ static_cast<int>(nums.size()) };

        constexpr int offset{ 50 };

        std::vector<long long> dp_a_;
        std::vector<long long> dp_b_;

        dp_a_.resize(offset + 1, 0);
        dp_b_.resize(offset + 1);

        std::vector<long long>* dp_a{ &dp_a_ };
        std::vector<long long>* dp_b{ &dp_b_ };

        for (int k{ 0 }; k <= nums[0]; ++k) { dp_a_[k] = 1; }

        for (int i{ 1 }; i < size; ++i) {
            for (int k{ 0 }; k <= offset; ++k) {
                if (nums[i] < k) {
                    (*dp_b)[k] = 0;
                    continue;
                }

                long long ans{ 0 };

                int cur_1{ k };
                int cur_2{ nums[i] - cur_1 };

                for (int d{ 0 },
                     d_end{ std::min(cur_1, std::min(nums[i - 1], nums[i])) };
                     d <= d_end; ++d) {
                    int prv_1{ d };
                    int prv_2{ nums[i - 1] - prv_1 };

                    if (cur_2 <= prv_2) { ans = (ans + (*dp_a)[d]) % M; }
                }

                (*dp_b)[k] = ans;
            }

            std::swap(dp_a, dp_b);
        }

        long long ret{ 0 };

        for (int k{ 0 }; k <= nums[size - 1]; ++k) {
            ret = (ret + (*dp_a)[k]) % M;
        }

        return ret;
    }
};
