class Solution {
public:
    const int MOD = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(51, 0));
        for(int i = 0; i<=nums[n-1]; i++){
            dp[n-1][i] = 1;
        }
        for(int i=n-2; i>=0; i--){
            if(nums[i]<=nums[i+1]){
                int temp = (nums[i+1]-nums[i])%MOD;
                for(int j=0; j<=nums[i]; j++){
                    int cnt = 0;
                    for(int k=0; k<=50; k++){
                        if(k>=temp){
                             cnt=(dp[i+1][k]%MOD + cnt%MOD)%MOD;
                             cnt %= MOD;
                        }
                    }
                    dp[i][j] = cnt;
                    temp++;
                }
            }else{
                int temp = 0;
                for(int j=0; j<=nums[i+1]; j++){
                    int cnt = 0;
                    for(int k=0; k<=50; k++){
                        if(k>=temp){
                            cnt=(dp[i+1][k]%MOD + cnt%MOD)%MOD;
                            cnt %= MOD;
                        }
                    }
                    dp[i][j] = cnt;
                    temp++;
                }
            }
        }
        int ans = 0;
        for(int i=0; i<=50; i++){
            ans += dp[0][i];
            ans %= MOD;
        }
        return ans;
    }
};
