class Solution {
public:
    const int mod = 1000000007;
    long solve(int last, int idx, vector<int> &nums, int n,vector<vector<long>> &dp){
        if(idx == n){
            return 1;
        }
        if(dp[last][idx] != -1) return dp[last][idx];
        if(idx==0){
            long long ans = 0;
            for(int i=0;i<=nums[0];i++){
                ans += solve(i,idx+1, nums, n,dp);
                ans%=mod;
            }
            return dp[last][idx] = ans%mod;;
        }
        int l_inc = last;
        int l_dec = nums[idx-1]-last;
        long long ans = 0;
        for(int i=0;i<=nums[idx];i++){
            if(l_inc<= i && l_dec>=(nums[idx]-i)) ans += solve(i, idx+1, nums,n,dp);
            ans%=mod;
        }
        return dp[last][idx] = ans%mod;;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<long>> dp(50+1, vector<long>(n,-1));
        return solve(0,0,nums,n,dp)%mod;;
    }
};
