class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        M = 10 ** 9 + 7

        @lru_cache(None)
        def dp(idx, l1):
            if idx == len(nums):
                return 1
            l2 = float('inf') if idx == 0 else nums[idx - 1] - l1
            cur = 0
            for i in range(l1, nums[idx] + 1):
                n1 = i
                n2 = nums[idx] - i
                # print(n1, n2, l1, l2)
                if n1 >= l1 and n2 <= l2:
                    # print(dp(idx + 1, n1))
                    cur += dp(idx + 1, n1)
            return cur

        return dp(0, 0) % M
