MOD = int(1e9) + 7
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        def dfs(p: int, prev: int) -> int:
            if p == n:
                return 1
            key = (p, prev)
            if key in dp:
                return dp[key]
            ans = 0
            prev2 = nums[p] if p == 0 else (nums[p - 1] - prev)
            for v in range(prev, nums[p] + 1):
                if prev2 >= (nums[p] - v):
                    ans += dfs(p + 1, v)
            dp[key] = ans
            return ans
        dp = {}
        return dfs(0, 0) % MOD
