class Solution {
public:
    int mod = 1e9 + 7;  
    int solve(int i, int j, vector<int>& nums, vector<vector<int>>& dp) {
        if(j > nums[i])
            return 0;
        if(dp[i][j] != -1)
            return dp[i][j];
        if(i == nums.size() - 1)
            return dp[i][j] = (1 + solve(i, j+1, nums, dp)) % mod;
        return dp[i][j] = (solve(i+1, max(j, nums[i+1] - nums[i] + j), nums, dp) + solve(i, j+1, nums, dp)) % mod;
    }
    int countOfPairs(vector<int>& nums) {
        int maxEle = *max_element(nums.begin(), nums.end());
        vector<vector<int>> dp(nums.size(), vector<int>(maxEle + 1, -1));
        return solve(0, 0, nums, dp);
    }
};
