class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int mod = 1000000007;
        vector <int> diff(n, 0);
        for (int i=1; i<n; i++){
            diff[i] = nums[i] - nums[i-1];
        }
        int dp[51][n];
        for (int i=0; i<51; i++) for (int j=0; j<n; j++) dp[i][j] = 0;
        for (int i=0; i<=nums[0]; i++) dp[i][0] = 1;
        // for (int i=0; i<=5; i++){
        //     for (int j=0; j<n; j++){
        //         cout << dp[i][j] << " ";
        //     }
        //     cout << endl;
        // }
        // cout << endl;
        for (int i=1; i<n; i++){
            int end = nums[i];
            for (int j=0; j<=end; j++){
                int end2 = j - max(0, diff[i]);
                // cout << i << " " << j << " " << end2 << endl;
                for (int k=0; k<=end2; k++){
                    dp[j][i] = ((dp[j][i] % mod) + (dp[k][i-1] % mod)) % mod;
                }
            }
        }
        // for (int i=0; i<=5; i++){
        //     for (int j=0; j<n; j++){
        //         cout << dp[i][j] << " ";
        //     }
        //     cout << endl;
        // }
        int sum = 0;
        for (int i=0; i<51; i++) sum = ((sum % mod) + (dp[i][n-1] % mod)) % mod;
        return sum;
    }
};
