class Solution(object):
    def countOfPairs(self, nums):
        n = len(nums)
        m = max(nums) + 1

        MOD = int(1e9) + 7

        dp = [[0]*m for _ in range(n)]

        for u in range(nums[0]+ 1):
            dp[0][u] = 1

        for i in range(1,n):
            for u in range(nums[i] + 1):
                v = nums[i] - u

                for uu in range(nums[i-1] + 1):
                    if uu > u:
                        break

                    vv = nums[i-1] - uu

                    if vv < v:
                        break

                    dp[i][u] += dp[i-1][uu]
                    dp[i][u] %= MOD

        return sum(dp[n-1][u] for u in range(m)) % MOD
