#define MOD 1000000007

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int dp[n][51];        // the last number in arr[1] == j

        memset(dp,0,n*51*sizeof(int));

        for(int j=0;j<=50;j++)  dp[0][j] = (j<=nums[0]);

        for(int i=1;i<n;i++) {
            for(int j=0;j<=nums[i-1];j++) {
                for(int k=j;k<=nums[i];k++) {
                    if(nums[i-1]-j >= nums[i]-k) {
                        dp[i][k] += dp[i-1][j];
                        dp[i][k] %= MOD;
                    }
                }
            }
        }

        int ret = 0;
        for(int j=0;j<=50;j++) {
            ret+=dp[n-1][j];
            ret%=MOD;
        }
        return ret;
    }
};
