class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        # dp[i][j]: arr1[i] ends with j, arr2[i] ends with nums[i]-j
        n = len(nums)
        mod = int(1e9+7)
        dp = [[0]*51 for i in range(n)]
        for j in range(nums[0]+1):
            dp[0][j] = 1
        for i in range(1,n):
            # current sum(arr1[i],arr2[i]) is nums[i]
            for j in range(nums[i]+1):
                num1,num2 = j,nums[i]-j
                for k in range(num1+1):
                    # look at previous state dp[i-1][k] for all k <= num1
                    # print(f'nums1:{num1},nums2:{num2},prev1:{k}, prev2:{nums[i-1]-k}')
                    if num2 <= nums[i-1]-k:
                        dp[i][j] = (dp[i][j]+dp[i-1][k])%mod
        # print(dp)
        return sum(dp[n-1])%mod
