using namespace std;
#include<bits/stdc++.h>
#define ll long long
#define pb push_back
#define all(x) x.begin(), x.end()
#define float long double
#define double long double
#define sortall(x) sort(all(x))
#define sz(a) (int)(a).size()
const int mod = 1e9+7;
//<--------------------------------------------------------------------------------->

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
     int n = sz(nums);
      vector<int> mini;
      int maxi = 0;
      mini.pb(0);
      int mini1 = nums[0];
      for(int i=1;i<n;i++){
          maxi =  maxi + (((nums[i]-nums[i-1])>=0)?nums[i]-nums[i-1]:0);
          if(maxi>nums[i])return 0;
          mini.pb(maxi);
      }  
      vector<vector<int>> dp(55,vector<int> (n+5,0));
      for(int i=0;i<n;i++){
        for(int j=0;j<=nums[i] - mini[i];j++){
            if(i==0)
                dp[j][i] = 1;
            else  dp[j][i] = (((j-1)>=0)?dp[j-1][i]:0) + dp[j][i-1];  
            dp[j][i] %= mod;
        }
      }
      int ans = 0;
      for(int j = 0;j<=nums[n-1] - mini[n-1];j++){
              ans+= dp[j][n-1];
              ans %=mod;
      }
      return ans;
    }
};
