class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int[] dp = new int[51];
        for(int i = 0; i <= nums[0]; i++) {
            dp[i] = i + 1;
        }
        int mod = 1_000_000_007;
        for(int i = 1; i < n; i++) {
            int[] dp2 = new int[51];
            int prev = nums[i-1];
            int num = nums[i];
            for(int j = 0; j <= num; j++) {
                int j2 = num - j;
                int k = Math.min(prev - j2, j);
                if(k >= 0) {
                    dp2[j] = dp[k];
                }
                if(j > 0) {
                    dp2[j] = (dp2[j] + dp2[j-1]) % mod;
                }
            }
            //System.out.println(Arrays.toString(dp2));
            dp = dp2;
        }
        return dp[nums[n-1]];
    }
}
