class Solution:
    def countOfPairs(self, a: List[int]) -> int:
        n = len(a)
        mod = 10 ** 9 + 7
        @cache
        def sol(idx, prev):
            if(idx == n):
                return 1
            ans = 0
            val = a[idx]
            for i in range(val + 1):
                v1 = i
                v2 = val - i
                if(prev == -1):
                    ans += sol(idx + 1, v1)
                    ans %= mod
                else:
                    p1 = prev
                    p2 = a[idx - 1] - prev
                    if(v1 >= p1 and v2 <= p2):
                        ans += sol(idx + 1, v1)
                        ans %= mod
            ans %= mod
            return ans
        ans = sol(0, -1)
        ans %= mod
        return ans
