class Solution {
public:
    int dp[2001][51];
    int rec(int index,int last,vector<int>& nums){
        if(index==nums.size()){
            return 1;
        }
        if(dp[index][last]!=-1){
            return dp[index][last];
        }
        int mx=nums[index];
        int res=0;
        int mod=(int)1e9+7;
        for(int i=last;i<=mx;i++){

            int counter=nums[index]-i;
            int prevCounter=nums[index-1]-last;
            if(counter>=0 and counter<=prevCounter){
                res=(res%mod+(rec(index+1,i,nums)))%mod;


            }
        }
        return dp[index][last]=res;
    }
    int countOfPairs(vector<int>& nums) {
        int mx=nums[0];
        int ans=0;
        int mod=(int)1e9+7;
        memset(dp,-1,sizeof(dp));

        for(int i=0;i<=mx;i++){
            ans=(ans%mod)+(rec(1,i,nums)%mod);
            ans=ans%mod;


        }
        return ans;

    }
};
