class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10 ** 9 + 7
        n = len(nums
        )
        @cache
        def solve(pos, v1, v2):
            if pos >= n: 
                return 1

            ret = 0

            for i in range(v1, nums[pos] + 1):
                if nums[pos] - i > v2:
                    continue
                ret += solve(pos + 1, i, nums[pos] - i)

            return ret

        return solve(0, 0, 100000) % mod
