class Solution {
public:
    #define ll long long int 
    const ll mod = 1e9 + 7;
    ll dp[2005][1005];

    ll min(ll a, ll b) {
        if(a <= b) return a;
        else return b;
    }

    ll add(ll a, ll b)
    {
        a %= mod; b %= mod;
        ll ans = (a + b) % mod;
        return ans % mod;
    }

    ll find(vector<int> &nums, ll n, ll a, ll b, ll ind)
    {
        // cout<<a<<" "<<b<<" "<<ind<<endl;
        if(ind == n) return 1;

        if(dp[ind][a] != -1) return dp[ind][a];

        ll ans = 0;
        ll i = a;
        while(i <= nums[ind]) {
            ll x = i; ll y = nums[ind] - i;
            if(a <= x && b >= y) {
                ll d = find(nums, n, x, y, ind+1);
                ans = add(ans , d) % mod;
            }
            i++;
        }
        dp[ind][a] = ans%mod;
        return ans%mod;
    }


    int countOfPairs(vector<int>& nums) {
        ll n = nums.size();
        for(ll i=0; i<2005; i++) {
            for(ll j=0; j<1005; j++) {
                dp[i][j] = -1;
            }
        }
        return find(nums, n, 0, 1000, 0);
    }
};
