using ll = long long;
constexpr ll mod = 1e9 + 7;


class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int size = nums.size();
        vector<vector<vector<ll>>> dp(size, vector<vector<ll>>(51, vector<ll>(51)));
        for (int a = nums[0]; a >= 0; --a) {
            if (nums[0] - a >= 0)
                dp[0][a][nums[0] - a] = 1;
        }
        int num;
        int num1;
        int b, bb;
        for (int i = 1; i < size; ++i) {
            num = nums[i];
            for (int a = 0; a <= 50; ++a) {
                b = num - a;
                if (b < 0)
                    continue;
                for (int aa = 0; aa <= a; ++aa) {
                    num1 = nums[i - 1];
                    bb = num1 - aa;
                    if (bb < b)
                        continue;
                    dp[i][a][b] += dp[i - 1][aa][bb];
                    dp[i][a][b] %= mod;
                }
            }
        }
        ll ans = 0;
        num = nums.back();
        for (int a = 0; a <= num; ++a) {
            ans += dp[size - 1][a][num - a];
            ans %= mod;
        }
        return ans;
    }
};
