class Solution {
public:

    int sol(vector<vector<int>> &dp,vector<int>&nums,int i,int prev){
        if(i==nums.size()){
            return 1;
        }
        if(dp[i][prev+1]!=-1){
            return dp[i][prev+1]%1000000007;
        }
        int s=0;
        if(i==0){
            for(int j=prev+1;j<=nums[i];j++){
                s=(s+sol(dp,nums,i+1,j)%1000000007)%1000000007;
            }
        }
        else{
            for(int j=prev;j<=nums[i];j++){
                if(nums[i-1]-prev >= nums[i]-j){
                    s=(s+sol(dp,nums,i+1,j)%1000000007)%1000000007;
                }
            }
        }
        return dp[i][prev+1]=s%1000000007;
    }


    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp(nums.size()+1,vector<int>(52,-1));
        return sol(dp,nums,0,-1)%1000000007;
    }
};
