class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int MOD = 1000000007;
        vector<vector<long long int>> dp(n, vector<long long int>(51, 0));
        for (int x1 = 0; x1 <= nums[0]; x1++) {
            dp[0][x1] = 1;
        }

        for (int pos = 1; pos < n; pos++) {
            int min_x1 = nums[pos] - nums[pos - 1];
            if (min_x1 < 0) min_x1 = 0;
            for (int x1 = min_x1; x1 <= nums[pos]; x1++) {
                int max_x11 = nums[pos - 1] - nums[pos] + x1;
                if (max_x11 > x1) max_x11 = x1;
                for (int x11 = 0; x11 <= max_x11; x11++) 
                    dp[pos][x1] += dp[pos - 1][x11];
                    dp[pos][x1] %= MOD;
            }
        }

        long long int output = 0;
        for (int x1 = 0; x1 <= nums[n - 1]; x1++) {
            output += dp[n - 1][x1];
            output %= MOD;
        }
        return output;
    }
};

