class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        constexpr int modulo = 1e9 + 7;
         int dp[2][51][51] {};
         int n = nums.size();
         std::fill(dp[n&1][0], dp[n&1][50]+51, 1);
         for (int i = n-1; i >= 0; --i) {
            int prev_num = i > 0 ? nums[i-1] : 50;
            for (int pinc {0}; pinc <= prev_num; ++pinc) {
               if (i == 0 && pinc > 0) continue;
               int pdec = prev_num - pinc;
               int& total = dp[i&1][pinc][pdec];
               total = 0;
               for (int x = max(pinc, nums[i]-pdec); x <= nums[i]; ++x) {
                  total = (total + dp[(i+1)&1][x][nums[i]-x]) % modulo;
               }
            }
         }
         return dp[0][0][50];
    }
};
