typedef long long ll;
const ll MOD = (ll)(1e9 + 7);
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<ll> ans(nums[0] + 1, 1LL);
        for (int i = 1; i < nums.size(); i++) {
            vector<ll> tmp(nums[i] + 1, 0LL);
            for (int j = 0; j <= nums[i - 1]; j++) {
                for (int k = 0; k <= nums[i]; k++) {
                    if (j <= k && nums[i - 1] - j >= nums[i] - k)
                        tmp[k] = (tmp[k] + ans[j]) % MOD;
                }
            }
            swap(ans, tmp);
        }
        ll res = 0LL;
        for (int i = 0; i < ans.size(); i++)
            res = (res + ans[i]) % MOD;
        return (int)res;
    }
};
