class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        @lru_cache(None)
        def dp(index, last1, last2):
            if index == n:
                return 1
            total_ways = 0
            for v1 in range(last1, nums[index] + 1):
                v2 = nums[index] - v1
                if v2 <= last2:
                    total_ways = (total_ways + dp(index + 1, v1, v2)) % MOD
            return total_ways
        return dp(0, 0, nums[0])
