class Solution {
public:
    int f[2001][51][51];
    int countOfPairs(vector<int>& nums) {
        int mod = 1e9 + 7;
        int n = nums.size();
        int maxNum = *max_element(nums.begin(), nums.end());
        for(int i = 0; i < n; i++)
            for(int j = 0; j < maxNum; j++)
                for(int k = 0; k < maxNum; k++)
                    f[i][j][k] = 0;


        for(int i = 0; i < n; i++)
        {
            if(i == 0)
                for(int j = 0; j <= nums[i]; j++)
                    f[i][j][nums[i] - j] = 1;
            else
                for(int j = 0; j <= nums[i]; j++)
                    for(int k = 0; k <= j; k++)
                        if(nums[i] - j <= nums[i - 1] - k)
                            f[i][j][nums[i] - j] = (f[i][j][nums[i] - j] + f[i - 1][k][nums[i - 1] - k] % mod) % mod;
        }
        int result = 0;
        for (int j = 0; j <= nums[n-1]; ++j) {
            result = (result + f[n-1][j][nums[n - 1] - j]) % mod;
        }
        return result;
    }
};
