/**
 * @param {number[]} nums
 * @return {number}
 */
var countOfPairs = function(nums) {
    let prev = Array(51).fill(0);
    let next = Array(51).fill(0);

    for (let x = 0; x <= nums[0]; x++) {
        prev[x]++;
    }

    let MOD = Math.pow(10, 9) + 7;

    for (let i = 1; i < nums.length; i++) {
        if (nums[i] > nums[i-1]) {
            for (let x = nums[i] - nums[i-1]; x <= nums[i]; x++) {
                for (let y = 0; y <= nums[i-1] - nums[i] + x; y++) {
                    next[x] += prev[y];
                    next[x] %= MOD;
                }
            }
        } else {
            for (let x = 0; x <= nums[i]; x++) {
                for (let y = 0; y <= x; y++) {
                    next[x] += prev[y];
                    next[x] %= MOD;
                }
            }
        }
        prev = next;
        next = Array(51).fill(0);
    }

    return prev.reduce((accum, cur) => (accum + cur) % (MOD), 0);
};
