class Solution {
public:
	const long long MOD = 1e9 + 7;
	int countOfPairs(vector<int>& nums) {
		int n = nums.size();
		nums.insert(nums.begin(), 50);
		// at index i, arr1[i] = j
		vector<vector<int>>dp(n+1, vector<int>(51,0));
		dp[0][0] = 1;
		for (int i = 1; i <= n; i++) {
			for (int j = 0; j <= nums[i - 1]; j++) {
				int r = nums[i - 1] - j;
				for (int k = j; k <= nums[i]; k++) {
					if (nums[i] - k <= r)dp[i][k] = (dp[i][k] + dp[i - 1][j]) % MOD;
				}
			}
		}
		long long ret = 0;
		for (int i = 0; i <= 50; i++)ret = (ret + dp[n][i]) % MOD;
		return ret;
	}
};
