class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        pairs = [0] * 51 # pairs[i] for arr1
        for i in range(nums[0]+1):
            pairs[i] = 1
        for i in range(1, len(nums)):
            s = nums[i]
            pre = nums[i-1]
            tmp = [0] * 51
            for a in range(s+1):
                b = s - a
                for prea in range(a+1):
                    preb = pre - prea
                    if preb < 0 or preb < b:
                        break
                    tmp[a] = (tmp[a] + pairs[prea]) % 1000000007
            pairs = tmp
        return sum(pairs) % 1000000007
