class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = ((10 ** 9) + 7)
        n = len(nums)

        @functools.lru_cache(None)
        def dp(x1, x2, i):
            if i == n:
                return 1
            t = nums[i]
            v = 0
            for y1 in range(x1, t + 1):
                y2 = t - y1
                if 0 <= y2 <= x2:
                    v += dp(y1, y2, i + 1)
                    v %= mod
            return v

        sol = dp(0, float('inf'), 0)
        dp.cache_clear()
        return sol % mod
