class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        long long mod = (1e9 + 7)*1LL;

        long long dp[n + 1][1010];
        memset(dp, 0, sizeof(dp));

        for(int j = 0 ; j <= nums[0] ; j++){
            dp[1][j] = 1*1LL;
        }

        for(int i = 2 ; i <= n ; i++){
            // if(i == 2)
            // cout<<"okay so i is "<<i<<endl;
            for(int j = 0 ; j <= nums[i - 1] ; j++){
                int z = nums[i - 1] - j;

                for(int j1 = 0 ; j1 <= nums[i - 2] ; j1++){
                    int z1 = nums[i - 2] - j1;
                    // if(i == 2)
                    // cout<<"okay so "<<j<<"  "<<j1<<"  "<<z<<"   "<<z1<<endl;
                    if(j >= j1 && z <= z1){
                       // if(i == 2)
                    //cout<<"entry "<<dp[i - 1][j1]<<endl;

                        dp[i][j] += dp[i - 1][j1];
                        dp[i][j] %= mod;
                    }

                }
                //cout<<"for j "<<j<<"  "<<dp[i][j]<<endl;
            }

        }


        // for(int i = 1 ; i <= n ; i++){
        //     for(int j = 0 ; j <= 3 ; j++){
        //         cout<<dp[i][j]<<" ";
        //     }
        //     cout<<endl;
        // }

        long long ans = 0;

        for(int j = 0 ; j <= nums[n - 1] ; j++){
            //cout<<"okay val is "<<j<<"  "<<dp[n - 1][j]<<endl;
            ans += dp[n][j];
            ans %= mod;
        }

        return ans;

    }
};
