	const ll INF= LONG_LONG_MAX;
int mod=1e9+7;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
		ll n=nums.size();
		ll m=52;

		vector<vector<ll>> dp(n+1,vector<ll>(m,0));
		for(int i=0;i<=nums[0];i++)
        {
            dp[0][i]=1;

        }

		loop(i,1,n)
		{
			ll cur=nums[i],prev=nums[i-1];
			for(ll j=0;j<=cur;j++)
			{
				for(ll k=0;k<=j;k++)
				{
                    ll icur2=cur-j;
                    ll pcur2=prev-k;
                    if(icur2>pcur2) continue;
                    if(pcur2>prev || pcur2<0) continue;


					// ll tmp=(dp[i-1][k]*dp[i-1][q])%mod;
					dp[i][j]=(dp[i][j]+dp[i-1][k]) %mod;



				}


			}

		}
		ll ans=0;
		for(int i=0;i<=m;i++)
		{
			if(i>nums[n-1]) break;
			ans=(ans+dp[n-1][i])%mod;
		}
        // for(auto &i:dp) 
        // {
        //     for(auto  &j:i) cout<<j<<" ";
        //     cout<<endl;
        // }
		return ans;




    }
};
