class Solution {
public:
    vector<vector<long long>> dp;
    int mod=(int)1e9+7;
    long long rec(vector<int>&nums,int i,int pv1){
        if(i>=nums.size()){
            return 1;
        }
        int pv2=INT_MAX;
        if(i>0){
            pv2=nums[i-1]-pv1;
        }
        if(pv1!=-1 && dp[i][pv1]!=-1){
            return dp[i][pv1];
        }
        //cout<<i<<" "<<pv1<<" "<<pv2<<endl;
        long long ans=0;
        for(int j=max(0,pv1);j<=nums[i];j++){
            if(nums[i]-j<=pv2)
            ans=(ans+rec(nums,i+1,j))%mod;
        }
         if(pv1!=-1){
             dp[i][pv1]=ans;
        }
        return ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        dp.resize(nums.size(),vector<long long>(51,-1));
        return rec(nums,0,-1);
    }
};
