class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length, MOD = 1_000_000_007;
        int[][] dp = new int[n][51];
        for (int l = 0; l <= nums[0]; ++l) {
            dp[0][l]++;
        }
        for (int i = 1; i < n; ++i) {
            for (int l = 0; l <= nums[i - 1]; ++l) {
                int r = nums[i - 1] - l;
                for (int l1 = 0; l1 <= nums[i]; ++l1) {
                    int r1 = nums[i] - l1;
                    if (l <= l1 && r >= r1) {
                        dp[i][l1] = (dp[i][l1] + dp[i - 1][l]) % MOD;
                    }
                }
            }
            // for (int j = 0; j <= nums[i]; ++j) {
            //     System.out.println(dp[i][j]);
            // }
            // System.out.println("----------");
        }


        int ans = 0;
        for (int i = 0; i <= nums[n - 1]; ++i) {
            ans = (ans + dp[n - 1][i]) % MOD;
        }
        return ans;
    }
}
