class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int mod = 1e9 + 7;
        int n = nums.size();
        vector<int> A, B;

        vector<vector<int>> dp(2010, vector<int>(51, -1));

        auto recur = [&](auto&& recur, vector<int>& a, vector<int>& b) -> int {
            int at = a.size();
            if(at == n)
                return 1;

            int al = (at == 0 ? 0 : a.back());
            if(dp[at][al] != -1)
                return dp[at][al];

            int ans = 0;
            int ar = nums[at];
            for(int i = al; i <= ar; i++) {
                int j = nums[at] - i;
                if(at > 0 && b.back() < j)
                    continue;
                a.push_back(i);
                b.push_back(j);
                ans = (ans + recur(recur, a, b)) % mod;
                a.pop_back();
                b.pop_back();
            }
            return dp[at][al] = ans;
        };

        return recur(recur, A, B);
    }
};
