class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<int>dp(1001,0);
        int mod=1e9+7;

        for(int i=0;i<=nums[0];i++){
            dp[i]=1;

        }
        for(int i=1;i<=1000;i++){
            dp[i]+=dp[i-1];
            dp[i]=dp[i]%mod;
            }
        int ans=0;
        for(int i=1;i<n;i++){
            ans=0;
            vector<int>dp1(1001,0);
            for(int j=0;j<=nums[i];j++){
                int p=min(j,nums[i-1]);
                int k=min(p,nums[i-1]-(nums[i]-j));
                 if(k>=0)
                 {
                    dp1[j]=dp[k];
                    dp1[j]=dp1[j]%mod;

                 }
            }
            for(int i=1;i<=1000;i++){
            dp1[i]+=dp1[i-1];
            dp1[i]=dp1[i]%mod;
            }
            swap(dp,dp1);

        }
        return dp[1000]%mod;

        }


};
